import React, { Component, useState } from "react";
import Select from "react-select";
import api from '../../api'


class SelectorMedico extends Component {
  constructor(props) {
    super(props);
    this.state = {
      medicos : [],
      value : '',
    };
  }
  

  componentWillMount = async () => {
    

    await api.getMedics()
    .then(medicos => {this.setState({
        medicos: medicos.data.data,
        selected : '',
    })
    })}


  onChange = selectedOption => {
    const medico = event.target.validity.valid
    this.setState({ value: selectedOption });
    console.log(`Option selected:`, selectedOption);
    setMedico(selectedOption.val);
    
  };

  render() {
    const { medicos } = this.state
    const selectmedico = [];
    medicos.map((medico) => { selectmedico.push({ desc:medico.medico.nombre+" "+medico.medico.apellidos, val: medico.medico._id  })});
    return (
      <Select
      options={selectmedico} getOptionLabel={(option)=>option.desc}
      getOptionValue={(option)=>option.val }
        onChange={this.onChange}
        value={this.state.value}
       
      />
    );
  }
}

export default SelectorMedico;
