import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import UserContext from "../../context/userContext";
function AuthOptions () {
const { userData, setUserData } = useContext(UserContext);
const history = useHistory();
const register = () => history.push("/register");
const login = () => history.push("/login");
const registerMedic = () => history.push("/registerMedic");
const consultas = () => history.push("/consultas");
const logout = () => {
setUserData({
token: undefined,
user: undefined
})
localStorage.setItem("auth-token","");
};
if(userData.user){
    return (<nav className="auth-options">
        <div class = "button-wrap">
      <button class = "rad-button wwt flat" onClick={logout}>Logout</button>
    </div>
    <br></br>
    <div class = "button-wrap">
      <button class = "rad-button wwt flat" onClick={consultas}>Consultas</button>
    </div>
</nav>)
}else{
    return (<nav className="auth-options">
                    <div class = "button-wrap">
                    <button class = "rad-button wwt flat" onClick={register}>Registrar</button>
                    </div>
                    <br></br>
                    <div class = "button-wrap">
                    <button class = "rad-button wwt flat" onClick={registerMedic}>Registrar m√©dico</button>
                    </div>
                    <br></br>
                    <div class = "button-wrap">
                    <button class = "rad-button wwt flat" onClick={login}>Login</button>
                    </div>
            </nav>)
}
}
export default AuthOptions;
