import React, { useState, useContext,useEffect } from 'react';
import { useHistory } from "react-router-dom";
import axios from "axios";
import ErrorNotice from "../../components/misc/ErrorNotice";
import UserContext from "../../context/userContext";
function CrearConsulta () {
const [fecha, setFecha] = useState();
const [motivo, setMotivo] = useState();
const [descripcion, setDescripcion] = useState();
const [paciente, setPaciente] = useState();
const [medico, setMedico] = useState();
const { userData } = useContext(UserContext);
const [error, setError] = useState();
const history = useHistory();
const submit = async (e) => {
e.preventDefault();
try{
const newUser = {fecha,motivo,descripcion,paciente,medico};
await axios.post("http://localhost:5000/enquiries/add", newUser);
let consultas= (await axios.get('http://localhost:5000/enquiries')).data;

history.push("/enquiries");
} catch(err) {
err.response.data.msg && setError(err.response.data.msg)
}
};
if(userData.user){
    return (
    <div className="crearQuedada">
    <h2>Crear quedada</h2>
    {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
    <form onSubmit={submit}>
    <label>Fecha: </label><br></br>
    <input type="date" id="fecha" onChange={e => setFecha(e.target.value)}/><br></br>
    <label>Motivo: </label><br></br>
    <input type="text" id="motivo" onChange={e => setMotivo(e.target.value)}/><br></br>
    <label>Descripción: </label><br></br>
    <input type="text" id="descripcion" onChange={e => setDescripcion(e.target.value)}/><br></br>
    <label>Paciente ID: </label><br></br>
    <input type="text" id="paciente" onChange={e => setPaciente(e.target.value)}/><br></br>
    <label>Médico ID: </label><br></br>
    <input type="text" id="medico" onChange={e => setMedico(e.target.value)}/><br></br>
    <input type="submit" value="Register" className="btn btn-primary" />
    </form>
    </div>
    );
    }else{
        history.push("/login");
    }
}
export default CrearConsulta;
