import React, { useState, useContext,useEffect } from 'react';
import { useHistory, Redirect } from "react-router-dom";
import axios from "axios";
import ErrorNotice from "../../components/misc/ErrorNotice";
import UserContext from "../../context/userContext";
import api from '../../api'
function EditarConsulta2() {
const { id } = useParams();
const consulta = api.getConsultaById(id)
this.setState({
    fecha2: consulta.data.data.fecha,
    motivo2: consulta.data.data.motivo,
})
const [fecha, setFecha] = useState();
const [motivo, setMotivo] = useState();
const { userData } = useContext(UserContext);
const [error, setError] = useState();
const history = useHistory();
const submit = async (e) => {
e.preventDefault();
try{
await api.editarConsulta(id, payload).then(res => {
    console.log(`Consulta updated successfully`)
     this.setState({
         fecha: fecha,
         motivo: motivo,
     })

history.push("/enquiries");
})
} catch(err) {
err.response.data.msg && setError(err.response.data.msg)
}
};
if(userData.user){
    return (
    <div className="crearQuedada">
    <h2>Crear quedada</h2>
    {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
    <form onSubmit={submit}>
    <label>Fecha: </label><br></br>
    <input type="date" id="fecha" value={fecha2} onChange={e => setFecha(e.target.value)}/><br></br>
    <label>Motivo: </label><br></br>
    <input type="text" id="motivo" value={motivo2} onChange={e => setMotivo(e.target.value)}/><br></br>
    </form>
    </div>
    );
    }else{
        return(
            <Redirect to={"/login"}/> 
        );
    }
}
export default EditarConsulta2;
