export default class Enfermedades extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            rol: this.props.userData.rol,
            loading: true,
            consulta:null,
        }
    }

    async componentWillMount(){
        const { id } = this.state
        const { rol } = this.state
        let url;
        if(rol=="Paciente"){
            url = await api.getEnfermedadByPacienteId(id)
        }else{
            url = await api.getEnfermedadByMedicoId(id)
        }
        // const response = await fetch(url);
        // const data = await response.json();
        this.setState({enfermedad: url.data.data, loading:false, rol:rol});
    }
    

    render(){
        return(
            <div>
                {this.state.loading || !this.state.enfermedad ? (
                    <div>loading...</div>
                ):(
                    <div>
                        <Prueba enfermedad={this.state.enfermedad} rol={this.state.rol}/>
                        
                    </div>
                )}
            </div>
        );
    }
}
