import React, { Component } from "react";
import { useHistory } from "react-router-dom";
import moment from 'moment';
import { useState, useContext } from 'react';
import axios from "axios";
import ErrorNotice from "../../components/misc/ErrorNotice";
import UserContext from '../../context/userContext';
function CrearMensaje () {
    const {userData} = useContext(UserContext);
    const [mensaje, setMensaje] = useState();
    const [consulta, setConsulta] = useState();
    const [paciente, setPaciente] = useState();
    const [medico, setMedico] = useState();
    const fecha = new Date();
    const escritoPor = userData.user.nickname+" "+userData.user.rol;
    const [error, setError] = useState();
    const history = useHistory();
    const submit = async (e) => {
    e.preventDefault();
    try{
    const newMensaje = {mensaje,consulta,paciente,medico,fecha,escritoPor};
    await axios.post("http://localhost:5000/enquiries/addConsultas", newMensaje);
    
    window.location.reload(false);
    } catch(err) {
    err.response.data.msg && setError(err.response.data.msg)
    }
    };
    return (
    <div className="crearMensaje">
    <h2>Crear mensaje</h2>
    {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
    <form onSubmit={submit}>
    <label>Mensaje: </label><br></br>
    <textarea  id="mensaje" onChange={e => setMensaje(e.target.value)}/><br></br>
    <label>Consulta: </label><br></br>
    <input type="text" id="consulta" onChange={e => setConsulta(e.target.value)}/><br></br>
    <label>Paciente ID: </label><br></br>
    <input type="text" id="paciente" onChange={e => setPaciente(e.target.value)}/><br></br>
    <label>MÃ©dico ID: </label><br></br>
    <input type="text" id="medico" onChange={e => setMedico(e.target.value)}/><br></br>
    <input type="submit" value="Crear" className="btn btn-primary" />
    </form>
    </div>
    );
    }

function DivChat(mensajeId){
    const { userData } = useContext(UserContext);
    if(userData.user.id == mensajeId){
        return <div class="container darker"></div>;
    }else{
        return <div class="container"></div>;
    }
}

function Prueba(props) {
    const { userData } = useContext(UserContext);
    console.log(userData.user.id)
    const content = props.mensaje.map((mensaje) =>
      <div key={mensaje._id}>
          {() => this.DivChat(mensaje._id)}
        <p>ID: {mensaje._id}</p>
        <p>Mensaje: {mensaje.mensaje}</p>
        <p>Fecha: {moment(mensaje.fecha).format('DD/MM/yyyy HH:mm')}</p>
        <p>Escrito por: {mensaje.escritoPor}</p>
        <br></br>

      </div>
    );
    return (
      <div>
        {content}
      </div>
    );
  }


export default class Mensaje extends React.Component{
    state={
        loading: true,
        mensaje:null,
    };

    async componentWillMount(){
        const url = "http://localhost:5000/enquiries/:id/mensajes";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({mensaje: data.data, loading:false});
    }
    

    render(){
        return(
            <div>
                {this.state.loading || !this.state.mensaje ? (
                    <div>loading...</div>
                ):(
                    <div>
                        <Prueba mensaje={this.state.mensaje} />
                    </div>
                )}
                <CrearMensaje />
            </div>
        );
    }
}
