import React, { Component, useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
import moment from 'moment';
import api from '../../api'
import UserContext from "../../context/userContext";

function ListaInformes(props) {
    const history = useHistory();
    let content;
    let content2;
    if(props.rol=="Paciente"){
        content = props.informeSangre.map((informeSangre) =>
        <div class="cards">
            <br></br>
        <div class="cita" key={informeSangre._id}>
            <p>Tipo de informe: {informeSangre.tipoInforme}</p>
            <p>Fecha:{moment(informeSangre.fecha).format('DD/MM/yyyy')}</p>
            <p>Medico: {informeSangre.medico.nickname}</p>
            <p>Observaciones: {informeSangre.observaciones}</p>
        </div>
        </div>
        );
        content2 = props.informeOrina.map((informeOrina) =>
        <div class="cards">
            <br></br>
        <div class="cita" key={informeOrina._id}>
            <p>Tipo de informe: {informeOrina.tipoInforme}</p>
            <p>Fecha:{moment(informeOrina.fecha).format('DD/MM/yyyy')}</p>
            <p>Medico: {informeOrina.medico.nickname}</p>
            <p>Observaciones: {informeOrina.observaciones}</p>
        </div>
        </div>
        );
        return (
        <div>
            {content}
            {content2}
            <br></br>
        </div>
        );
        }else{
            content = props.informeSangre.map((informeSangre) =>
            <div class="cards">
                <br></br>
            <div class="cita" key={informeSangre._id}>
            <p>Tipo de informe: {informeSangre.tipoInforme}</p>
            <p>Fecha:{moment(informeSangre.fecha).format('DD/MM/yyyy')}</p>
            <p>Paciente: {informeSangre.paciente.nickname}</p>
            <p>Observaciones: {informeSangre.observaciones}</p>
                {/* <div class="consultaBotones">
                <EditarCita id={cita._id} />
                </div>
                <div class="consultaBotones">
                <EliminarCita id={cita._id}/>
                </div>
                <div class="consultaBotones">
                <CrearInforme paciente={cita.paciente} id={cita._id} tipoCita={cita.tipoCita} tipoAnalisis={cita.tipoAnalisis}/>
                </div> */}

            </div>
            </div>
            );
            content2 = props.informeOrina.map((informeOrina) =>
            <div class="cita">
                <br></br>
            <div class="informeOrina" key={informeOrina._id}>
            <p>Tipo de informe: {informeOrina.tipoInforme}</p>
            <p>Fecha:{moment(informeOrina.fecha).format('DD/MM/yyyy')}</p>
            <p>Paciente: {informeOrina.paciente.nickname}</p>
            <p>Observaciones: {informeOrina.observaciones}</p>
                {/* <div class="consultaBotones">
                <EditarCita id={cita._id} />
                </div>
                <div class="consultaBotones">
                <EliminarCita id={cita._id}/>
                </div>
                <div class="consultaBotones">
                <CrearInforme paciente={cita.paciente} id={cita._id} tipoCita={cita.tipoCita} tipoAnalisis={cita.tipoAnalisis}/>
                </div> */}

            </div>
            </div>
            );
            return (
            <div>
                {content}
                {content2}
                <br></br>
            </div>
            );
        }
  }

  function TipoAnalisis(props) {
    if(props.tipoCita=="análisis"){
    return(
        <p>Tipo de análisis: {props.cita.tipoAnalisis}</p>
    );
    }else{
    return(
        <p></p>
    );  
    }
}


    function CrearInforme(props) {
        const history = useHistory();
        const { userData } = useContext(UserContext);
        let crearInforme;
        if(props.tipoCita=="análisis" && props.tipoAnalisis=="sangre"){
            crearInforme = () => history.push("/"+props.paciente._id+"/citas/"+props.id+"/"+props.tipoCita+"/"+props.tipoAnalisis+"/crearInformeSangre");
        }else if(props.tipoCita=="análisis" && props.tipoAnalisis=="orina"){
            crearInforme = () => history.push("/"+props.paciente._id+"/citas/"+props.id+"/"+props.tipoCita+"/"+props.tipoAnalisis+"/crearInformeOrina");
        }else if(props.tipoCita=="prueba médica"){
            crearInforme = () => history.push("/"+props.paciente._id+"/citas/"+props.id+"/"+props.tipoCita+"/crearInformePrueba");
        }else if(props.tipoCita=="presencial"){
            crearInforme = () => history.push("/"+props.paciente._id+"/citas/"+props.id+"/"+props.tipoCita+"/crearInformePresencial");
        }
        return(
            <button className="rad-button-2 wwt flat" onClick={crearInforme}>Crear informe</button>
        );
    }

    function EditarCita(props) {
        const history = useHistory();
        const { userData } = useContext(UserContext);
        const editarCita = () => history.push("/"+userData.user.id+"/citas/"+props.id+"/edit");
        return(
            <button className="rad-button-2 wwt flat" onClick={editarCita}>Editar cita</button>
        );
    }

    function EliminarCita(props) {
        const history = useHistory();
        const eliminarCita = () => {
            api.eliminarCita(props.id)
            window.location.reload()
        }
        return(
            <button className="rad-button-2 wwt flat" onClick={eliminarCita}>Eliminar cita</button>
        );
    }


export default class Informes extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            rol: this.props.userData.rol,
        }
    }

    async componentWillMount(){
        const { id } = this.state
        const { rol } = this.state
        let url;
        let url2;
        if(rol=="Paciente"){
            url = await api.getInformeAnalisisSangreByPaciente(id)
            url2 = await api.getInformeAnalisisOrinaByPaciente(id)
        }else{
            url = await api.getInformeAnalisisSangreByMedico(id)
            url2 = await api.getInformeAnalisisOrinaByMedico(id)
        }
        this.setState({informeSangre: url.data.data,informeOrina: url2.data.data, loading:false, rol:rol});
    }
    

    render(){
        return(
            <div>
                {this.state.loading || !this.state.informeSangre || !this.state.informeOrina ? (
                    <div>loading...</div>
                ):(
                    <div>
                        <ListaInformes informeSangre={this.state.informeSangre} informeOrina={this.state.informeOrina} rol={this.state.rol} />
                        
                    </div>
                )}
            </div>
        );
    }
}
