import React, { Component,useContext,useEffect } from 'react'
import api from '../../api'
import { useHistory,Redirect } from "react-router-dom";
import UserContext from "../../context/userContext";
import { useOktaAuth } from "@okta/okta-react";
import firebase from "firebase/app";
import moment from 'moment';
import "firebase/auth";

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`


class EditarConsulta extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            // idUser: this.props.match.params.idUser,
            idUser: this.props.userData.id,
            fecha: '',
            motivo: '',
        }
    }

    handleChangeInputFecha = async event => {
        const fecha = event.target.value
        this.setState({ fecha })
    }

    handleChangeInputMotivo = async event => {
        const motivo = event.target.validity.valid
            ? event.target.value
            : this.state.motivo

        this.setState({ motivo })
    }


    handleUpdateConsulta = async () => {
        const { id, fecha, motivo } = this.state
        const payload = { fecha, motivo }

        await api.editarConsulta(id, payload).then(res => {
           console.log(`Consulta updated successfully`)
            this.setState({
                fecha: '',
                motivo: '',
            })
            this.props.history.push('/');
        })
    }

    componentWillMount = async () => {
        const { id } = this.state
        const {idUser} =this.state
        console.log(idUser)
        const consulta = await api.getConsultaById(id)
        if(idUser != consulta.data.data.medico){
            this.props.history.push('/error');
        }
        this.setState({
            fecha: consulta.data.data.fecha,
            motivo: consulta.data.data.motivo,
        })
    }

    render() {
        const { fecha, motivo,id } = this.state
        return (
            <div className="form">
            <Wrapper>
                <Title>Editar consulta</Title>
                <div class="input">
                <div class="inputBox">
                <Label>Fecha: </Label>
                <InputText
                    type="text"
                    value={moment(fecha).format('DD/MM/yyyy HH:mm')}
                    onChange={this.handleChangeInputFecha}
                />
                </div>
                <div class="inputBox">
                <Label>Motivo: </Label>
                <InputText
                    type="text"
                    value={motivo}
                    onChange={this.handleChangeInputMotivo}
                />
                </div>
                <div class="inputBox">
                <Button onClick={this.handleUpdateConsulta}>Editar consulta</Button>
                </div>
                <div class="inputBox">
                <CancelButton href={id+'/consultas'}>Cancelar</CancelButton>
                </div>
                </div>
            </Wrapper>
            </div>
        )
}
}

export default EditarConsulta
