  function Citas(){
    const history = useHistory();
    const { userData } = useContext(UserContext);
    const citas = () => history.push("/"+userData.user.id+"/citas");
    return(
        <div class="citas">
        <button className="rad-button wwt flat" onClick={citas}>Ver citas</button>
        </div>
    );
    }

    function Informes(){
        const history = useHistory();
