import React, { Component, useState } from "react";
import Select from "react-select";
import api from '../../api'


class SelectorMedico extends Component {
  constructor(props) {
    super(props);
    this.state = {
      medicos : [],
      value : '',
    };
  }
  

  componentDidMount = async () => {
    

    await api.getMedicos()
    .then(pacientes => {this.setState({
        pacientes: pacientes.data.data,
        selected : '',
    })
    })}


  onChange = selectedOption => {
    const [pacient, setPacient] = useState();
    this.setState({ value: selectedOption });
    console.log(`Option selected:`, selectedOption);
    setPacient(selectedOption.val);
    
  };

  render() {
    const { pacientes } = this.state
    const selectpacient = [];
    pacientes.map((paciente) => { selectpacient.push({ desc:paciente.pacient.name+" "+paciente.pacient.surname, val: paciente.pacient._id  })});
    return (
      <Select
      options={selectpacient} getOptionLabel={(option)=>option.desc}
      getOptionValue={(option)=>option.val }
        onChange={this.onChange}
        value={this.state.value}
       
      />
    );
  }
}

export default PacientesSelect;
