import React, { Component } from "react";
import { useHistory } from "react-router-dom";
import moment from 'moment';

function Prueba(props) {
    const content = props.mensaje.map((mensaje) =>
      <div key={mensaje._id}>
        <p>ID: {mensaje._id}</p>
        <p>Mensaje: {mensaje.mensaje}</p>
        <p>Fecha: {moment(mensaje.fecha).format('DD/MM/yyyy HH:mm')}</p>

      </div>
    );
    return (
      <div>
        {content}
      </div>
    );
  }


export default class Mensaje extends React.Component{
    state={
        loading: true,
        mensaje:null,
    };

    async componentWillMount(){
        const url = "http://localhost:5000/enquiries/mensajes";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({mensaje: data.data, loading:false});
    }
    

    render(){
        return(
            <div>
                {this.state.loading || !this.state.mensaje ? (
                    <div>loading...</div>
                ):(
                    <div>
                        <Prueba mensaje={this.state.mensaje} />
                        
                    </div>
                )}
            </div>
        );
    }
}
