import React, { useState, useContext,useEffect  } from 'react';
import { useHistory, Redirect,useParams  } from "react-router-dom";
import axios from "axios";
import ErrorNotice from "../../components/misc/ErrorNotice";
import UserContext from "../../context/userContext";
import api from '../../api'
import Select from 'react-select';
import Async from 'react-async';
function CrearCita () {
const [fecha, setFecha] = useState();
const [ciudad, setCiudad] = useState();
const [direccion, setDireccion] = useState();
const [centroMedico, setCentroMedico] = useState();
const [motivo, setMotivo] = useState();
const [tipoCita, setTipoCita] = useState();
const { id } = useParams()
const { idMedico } = useParams()
const { userData, setUserData } = useContext(UserContext);
const consulta = id;
const paciente = userData.user.id;
const medico = idMedico;
const [error, setError] = useState();
const history = useHistory();
const onChange = async (selectedOption) => {

    console.log(`Option selected:`, selectedOption);
    setTipoCita(selectedOption.val);

    if(selectedOption.val=="anÃ¡lisis"){
        return (
            <Select options={selectcita} getOptionLabel={(option)=>option.desc}
            getOptionValue={(option)=>option.val } id="selectcita" placeholder="Selecciona una cita" onChange={onChange} ></Select>
            
        )
    }
    
  };

const submit = async (e) => {
e.preventDefault();
try{
const newCita = {fecha,ciudad,direccion,centroMedico,motivo,consulta,paciente,medico,tipoCita};
await axios.post("http://localhost:5000/citas/add", newCita);

history.push(userData.user.id+"/citas");
} catch(err) {
err.response.data.msg && setError(err.response.data.msg)
}
};

const sacaCitas = () =>
    api.getTipoCita()
    .then(res => {return res})

    return (
    <div className="crearCita">
    <h2>Crear cita</h2>
    {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
    <form onSubmit={submit}>
    <label>Fecha: </label><br></br>
    <input type="date" id="fecha" onChange={e => setFecha(e.target.value)}/><br></br>
    <label>Ciudad: </label><br></br>
    <input type="text" id="ciudad" onChange={e => setCiudad(e.target.value)}/><br></br>
    <label>DirecciÃ³n: </label><br></br>
    <input type="text" id="direccion" onChange={e => setDireccion(e.target.value)}/><br></br>
    <label>Centro MÃ©dico: </label><br></br>
    <input type="text" id="centroMedico" onChange={e => setCentroMedico(e.target.value)}/><br></br>
    <label>Motivo: </label><br></br>
    <input type="text" id="motivo" onChange={e => setMotivo(e.target.value)}/><br></br>
    <label>Tipo de Cita: </label><br></br>
    <Async promiseFn={sacaCitas}>

    {({ data, err }) => {
            if (err) return `Something went wrong: ${err.message}`

            if (data){

                const selectcita = [];
                data.data.data.map((cita) => { selectcita.push({ desc:cita, val: cita  })});

                return (
                    <Select options={selectcita} getOptionLabel={(option)=>option.desc}
                    getOptionValue={(option)=>option.val } id="selectcita" placeholder="Selecciona una cita" onChange={onChange} ></Select>
                    
                )}
            }}
    </Async>

    <input type="submit" value="Crear" className="btn btn-primary" />
    </form>
    </div>
    );
}
export default CrearCita;
