import React, { Component, useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
import moment from 'moment';
import api from '../../api'
import UserContext from "../../context/userContext";

function Prueba(props) {
    const history = useHistory();
    const { userData } = useContext(UserContext);
    useEffect(() => {
        if(!userData.user)
        history.push("/login");
        }, []);
    const mensajes = () => history.push("/mensajes");
    const crearMensaje = () => history.push("/crearMensaje");
    const content = props.consulta.map((consulta) =>
      <div key={consulta._id}>
        <p>ID: {consulta._id}</p>
        <p>Fecha:{moment(consulta.fecha).format('DD/MM/yyyy')}</p>
        <p>Paciente: {consulta.paciente}</p>
        <EditarConsulta id={consulta._id} />
        <EliminarConsulta id={consulta._id}/>
        <button className="btn btn-primary mr-2" onClick={mensajes}>Mensajes</button><br></br>
        <button className="btn btn-primary mr-2" onClick={crearMensaje}>Crear mensaje</button>

      </div>
    );
    return (
      <div>
        {content}
      </div>
    );
  }

  function Consultas(){
    const history = useHistory();
    const crearConsulta = () => history.push("/crearConsulta");
    return(
        <div>
        <button className="btn btn-primary mr-2" onClick={crearConsulta}>Crear consulta</button>
        </div>
    );
    }

    function EditarConsulta(props) {
        const history = useHistory();
        const editarConsulta = () => history.push("/"+props.id+"/edit");
        return(
            <div>
            <button className="btn btn-primary mr-2" onClick={editarConsulta}>Editar consulta</button>
            </div>
        );
    }

    function EliminarConsulta(props) {
        const history = useHistory();
        const eliminarConsulta = () => {
            api.eliminarConsulta(props.id)
            window.location.reload()
        }
        return(
            <div>
            <button className="btn btn-primary mr-2" onClick={eliminarConsulta}>Eliminar consulta</button>
            </div>
        );
    }

export default class Consulta extends React.Component{
    state={
        loading: true,
        consulta:null,
    };

    async componentWillMount(){
        const url = "http://localhost:5000/enquiries/";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({consulta: data.data, loading:false});
    }
    

    render(){
        return(
            <div>
                {this.state.loading || !this.state.consulta ? (
                    <div>loading...</div>
                ):(
                    <div>
                        <Prueba consulta={this.state.consulta} />
                        
                    </div>
                )}
                <Consultas />
            </div>
        );
    }
}
