import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'

import styled from 'styled-components'


const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class ListaConsulta extends Component {
    constructor(props) {
        super(props)
        this.state = {
            consultas: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.listaConsulta.then(consultas => {
            this.setState({
                consultas: consultas.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { consultas, isLoading } = this.state
        console.log('TCL: ConsultasList -> render -> consultas', consultas)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Fecha',
                accessor: 'fecha',
                filterable: true,
            },
            {
                Header: 'Motivo',
                accessor: 'motivo',
                filterable: true,
            },
            {
                Header: 'Descripción',
                accessor: 'descripcion',
                filterable: true,
            },
            {
                Header: 'Paciente',
                accessor: 'paciente',
                filterable: true,
            },
            {
                Header: 'Médico',
                accessor: 'medico',
                filterable: true,
            },
        ]

        let showTable = true
        if (!consultas.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={consultas}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default ListaConsulta
